INSERT INTO openchpl.cqm_criterion (title, nqf_number, description, cqm_criterion_type_id, last_modified_user, retired) VALUES
('Asthma Assessment', '0001', 'Percentage of patients aged 5 through 40 years with a diagnosis of asthma and who have been seen for at least 2 office visits, which were evaluated during at least one office visit within 12 months for the frequency (numeric) of daytime and nocturnal asthma symptoms.', 1, -1, true),
('Appropriate Testing for Children with Pharyngitis', '0002', 'Percentage of children 2-18 years of age, who were diagnosed with pharyngitis, dispensed an antibiotic and received a group A streptococcus (strep) test for the episode', 1, -1, true),
('Initiation and Engagement of Alcohol and Other Drug Dependence Treatment: a) Initiation, b) Engagement', '0004', 'Percentage of adolescent and adult patients with a new episode of alcohol and other drug (AOD) dependence who initiate treatment through an inpatient AOD admission, outpatient visit, intensive outpatient encounter or partial hospitalization within 14 days of the diagnosis and who initiated treatment and who had two or more additional services with an AOD diagnosis within 30 days of the initiation visit.', 1, -1, true),
('Prenatal Care: Screening for Human Immunodeficiency Virus (HIV)', '0012', 'Percentage of patients, regardless of age, who gave birth during a 12-month period who were screened for HIV infection during the first or second prenatal care visit.', 1, -1, true),
('Hypertension: Blood Pressure Measurement', '0013', 'Percentage of patient visits for patients aged 18 years and older with a diagnosis of hypertension who has been seen for at least 2 office visits, with blood pressure (BP) recorded.', 1, -1, true),
('Prenatal Care: Anti-D Immune Globulin', '0014', 'Percentage of D(Rh) negative, unsensitized patients, regardless of age, who gave birth during a 12-month period who received anti-D immune globulin at 26-30 weeks gestation.', 1, -1, true),
('Controlling High Blood Pressure', '0018', 'The percentage of patients 18-85 years of age who had a diagnosis of hypertension and whose BP was adequately controlled during the measurement year.', 1, -1, true),
('Weight Assessment and Counseling for Children and Adolescents', '0024', 'The percentage of patients 2 -17 years of age who had an outpatient visit with a Primary Care Physician (PCP) or OB/GYN and who had evidence of BMI percentile documentation, counseling for nutrition and counseling for physical activity during the measurement year.', 1, -1, true),
('Smoking and Tobacco Use Cessation, Medical assistance: a) Advising Smokers and Tobacco Users to Quit, b) Discussing Smoking and Tobacco Use Cessation Medications, c) Discussing Smoking and Tobacco Use Cessation Strategies', '0027', 'Percentage of patients 18 years of age and older who were current smokers or tobacco users, who were seen by a practitioner during the measurement year and who received advice to quit smoking or tobacco use or whose practitioner recommended or discussed smoking or tobacco use cessation medications, methods or strategies.', 1, -1, true),
('Preventive Care and Screening Measure Pair: a) Tobacco Use Assessment b) Tobacco Cessation Intervention', '0028', 'a) Tobacco Use Assessment. Description: Percentage of patients aged 18 years and older who have been seen for at least 2 office visits who were queried about tobacco use one or more times within 24 months. b) Tobacco Cessation Intervention Description: Percentage of patients aged 18 years and older identified as tobacco users within the past 24 months and have been seen for at least 2 office visits, who received cessation intervention', 1, -1, true),
('Breast Cancer Screening', '0031', 'Percentage of women 40-69 years of age who had a mammogram to screen for breast cancer.', 1, -1, true),
('Cervical Cancer Screening', '0032', 'The percentage of patients 18-85 years of age who had a diagnosis of hypertension and whose BP was adequately controlled during the measurement year', 1, -1, true),
('Chlamydia Screening for Women', '0033', 'Percentage of women 15- 24 years of age who were identified as sexually active and who had at least one test for Chlamydia during the measurement year.', 1, -1, true),
('Colorectal Cancer Screening', '0034', 'Percentage of adults 50-75 years of age who had appropriate screening for colorectal cancer.', 1, -1, true),
('Use of Appropriate Medications for Asthma', '0036', 'Percentage of patients 5-50 years of age who were identified as having persistent asthma and were appropriately prescribed medication during the measurement year. Report three age stratifications (5-11 years, 12-50 years, and total).', 1, -1, true),
('Childhood Immunization Status', '0038', 'Percentage of children 2 years of age who had four diphtheria, tetanus and acellular pertussis (DTaP); three polio (IPV); one measles, mumps and rubella (MMR); two H influenza type B (HiB); three hepatitis B (Hep B); one chicken pox (VZV); four pneumococcal conjugate (PCV); two hepatitis A (Hep A); two or three rotavirus (RV); and two influenza (flu) vaccines by their second birthday. The measure calculates a rate for each vaccine and two separate combination rates.', 1, -1, true),
('Preventive Care and Screening: Influenza Immunization for Patients >= 50 Years Old', '0041', 'Percentage of patients aged 50 years and older who received an influenza immunization during the flu season (September through February).', 1, -1, true),
('Pneumonia Vaccination Status for Older Adults', '0043', 'Percentage of patients 65 years of age and older who have ever received a pneumococcal vaccine.', 1, -1, true),
('Asthma Pharmacologic Therapy', '0047', 'Percentage of patients aged 5 through 40 years with a diagnosis of mild, moderate, or severe persistent asthma who were prescribed either the preferred long-term control medication (inhaled corticosteroid) or an acceptable alternative treatment.', 1, -1, true),
('Low Back Pain: Use of Imaging Studies', '0052', 'Percentage of patients with a primary diagnosis of low back pain who did not have an imaging study (plain x-ray, MRI, CT scan) within 28 days of diagnosis.', 1, -1, true),
('Diabetes: Eye Exam', '0055', 'Percentage of patients 18-75 years of age with diabetes (type 1 or type 2) who had a retinal or dilated eye exam or a negative retinal exam (no evidence of retinopathy) by an eye care professional.', 1, -1, true),
('Diabetes: Foot Exam', '0056', 'The percentage of patients aged 18-75 years with diabetes (type 1 or type 2) who had a foot exam (visual inspection, sensory exam with monofilament, or pulse exam).', 1, -1, true),
('Diabetes: HbA1c Poor Control', '0059', 'Percentage of patients 18-75 years of age with diabetes (type 1 or type 2) who had hemoglobin A1c > 9.0%.', 1, -1, true),
('Diabetes: Blood Pressure Management', '0061', 'Percentage of patients 18-75 years of age with diabetes (type 1 or type 2) who had blood pressure <140/90 mmHg.', 1, -1, true),
('Diabetes: Urine Screening', '0062', 'Percentage of patients 18-75 years of age with diabetes (type 1 or type 2) who had a nephropathy screening test or evidence of nephropathy.', 1, -1, true),
('Diabetes: LDL Management & Control', '0064', 'Percentage of patients 18-75 years of age with diabetes (type 1 or type 2) who had LDL-C < 100 mg/dL.', 1, -1, true),
('Coronary Artery Disease (CAD): Oral Antiplatelet Therapy Prescribed for Patients with CAD', '0067', 'Percentage of patients aged 18 years and older with a diagnosis of CAD who were prescribed oral antiplatelet therapy.', 1, -1, true),
('Ischemic Vascular Disease (IVD): Use of Aspirin or another Antithrombotic', '0068', 'Percentage of patients 18 years of age and older who were discharged alive for acute myocardial infarction (AMI), coronary artery bypass graft (CABG) or percutaneous transluminal coronary angioplasty (PTCA) from January 1-November 1 of the year prior to the measurement year, or who had a diagnosis of ischemic vascular disease (IVD) during the measurement year and the year prior to the measurement year and who had documentation of use of aspirin or another antithrombotic during the measurement year.', 1, -1, true),
('Coronary Artery Disease (CAD): Beta-Blocker Therapy for CAD Patients with Prior Myocardial Infarction (MI)', '0070', 'Percentage of patients aged 18 years and older with a diagnosis of CAD and prior MI who were prescribed beta-blocker therapy.', 1, -1, true),
('Ischemic Vascular Disease (IVD): Blood Pressure Management', '0073', 'Percentage of patients 18 years of age and older who were discharged alive for acute myocardial infarction (AMI), coronary artery bypass graft (CABG) or percutaneous transluminal coronary angioplasty (PTCA) from January 1-November 1 of the year prior to the measurement year, or who had a diagnosis of ischemic vascular disease (IVD) during the measurement year and the year prior to the measurement year and whose recent blood pressure is in control (<140/90 mmHg).', 1, -1, true),
('Coronary Artery Disease (CAD): Drug Therapy for Lowering LDL- Cholesterol', '0074', 'Percentage of patients aged 18 years and older with a diagnosis of CAD who was prescribed a lipid-lowering therapy (based on current ACC/AHA guidelines).', 1, -1, true),
('Ischemic Vascular Disease (IVD): Complete Lipid Panel and LDL Control', '0075', 'Percentage of patients 18 years of age and older who were discharged alive for acute myocardial infarction (AMI), coronary artery bypass graft (CABG) or percutaneous transluminal angioplasty (PTCA) from January 1-November1 of the year prior to the measurement year, or who had a diagnosis of ischemic vascular disease (IVD) during the measurement year and the year prior to the measurement year and who had a complete lipid profile performed during the measurement year and whose LDL-C<100 mg/dL.', 1, -1, true),
('Heart Failure (HF): Angiotensin- Converting Enzyme (ACE) Inhibitor or Angiotensin Receptor Blocker (ARB) Therapy for Left Ventricular Systolic Dysfunction (LVSD)', '0081', 'Percentage of patients aged 18 years and older with a diagnosis of heart failure and LVSD (LVEF < 40%) who were prescribed ACE inhibitor or ARB therapy.', 1, -1, true),
('Heart Failure (HF): Beta-Blocker Therapy for Left Ventricular Systolic Dysfunction (LVSD)', '0083', 'Percentage of patients aged 18 years and older with a diagnosis of heart failure who also have LVSD (LVEF < 40%) and who were prescribed beta-blocker therapy', 1, -1, true),
('Heart Failure (HF): Warfarin Therapy Patients with Atrial Fibrillation', '0084', 'Percentage of all patients aged 18 years and older with a diagnosis of heart failure and paroxysmal or chronic atrial fibrillation that were prescribed warfarin therapy.', 1, -1, true),
('Primary Open Angle Glaucoma (POAG): Optic Nerve Evaluation', '0086', 'Percentage of patients aged 18 years and older with a diagnosis of POAG who have been seen for at least two office visits who have an optic nerve head evaluation during one or more office visits within 12 months.', 1, -1, true),
('Diabetic Retinopathy: Documentation of Presence or Absence of Macular Edema and Level of Severity of Retinopathy', '0088', 'Percentage of patients aged 18 years and older with a diagnosis of diabetic retinopathy who had a dilated macular or fundus exam performed which included documentation of the level of severity of retinopathy and the presence or absence of macular edema during one or more office visits within 12 months.', 1, -1, true),
('Diabetic Retinopathy: Communication with the Physician Managing Ongoing Diabetes Care', '0089', 'Percentage of patients aged 18 years and older with a diagnosis of diabetic retinopathy who had a dilated macular or fundus exam performed with documented communication to the physician who manages the ongoing care of the patient with diabetes mellitus regarding the findings of the macular or fundus exam at least once within 12 months.', 1, -1, true),
('Anti-depressant medication management: (a) Effective Acute Phase Treatment, (b) Effective Continuation Phase Treatment', '0105', 'Percentage of patients 18 years of age and older who were diagnosed with a new episode of major depression, treated with antidepressant medication, and who remained on an antidepressant medication treatment', 1, -1, true),
('Venous Thromboembolism (VTE)-1 VTE prophylaxis', '0371', 'This measure assesses the number of patients who received VTE prophylaxis or have documentation why no VTE prophylaxis was given the day of or the day after hospital admission or surgery end date for surgeries that start the day of or the day after hospital admission.', 2, -1, true),
('VTE-2 Intensive Care Unit (ICU) VTE prophylaxis', '0372', 'This measure assesses the number of patients who received VTE prophylaxis or have documentation why no VTE prophylaxis was given the day of or the day after the initial admission (or transfer) to the Intensive Care Unit (ICU) or surgery end date for surgeries that start the day of or the day after ICU admission (or transfer).', 2, -1, true),
('VTE-3 VTE Patients with Overlap of Anticoagulation Therapy', '0373', 'This measure assesses the number of patients diagnosed with confirmed VTE who received an overlap of parenteral (intravenous [IV] or subcutaneous [subcu]) anticoagulation and warfarin therapy. For patients who received less than five days of overlap therapy, they must be discharged on both medications. Overlap therapy must be administered for at least five days with an international normalized ratio (INR) = 2 prior to discontinuation of the parenteral anticoagulation therapy or the patient must be discharged on both medications.', 2, -1, true),
('VTE-4 VTE Patients Unfractionated Heparin (UFH) Dosages/Platelet Count Monitoring by Protocol (or Nomogram) Receiving Unfractionated Heparin (UFH) with Dosages/ Platelet Count Monitored by Protocol (or Nomogram)', '0374', 'This measure assesses the number of patients diagnosed with confirmed VTE who received intravenous (IV) UFH therapy dosages AND had their platelet counts monitored using defined parameters such as a nomogram or protocol.', 2, -1, true),
('VTE-5 VTE discharge instructions', '0375', 'This measure assesses the number of patients diagnosed with confirmed VTE that are discharged to home, to home with home health, or home hospice on warfarin with written discharge instructions that address all four criteria: compliance issues, dietary advice, follow-up monitoring, and information about the potential for adverse drug reactions/interactions.', 2, -1, true),
('VTE-6 Incidence of potentially preventable VTE', '0376', 'This measure assesses the number of patients diagnosed with confirmed VTE during hospitalization (not present on arrival) who did not receive VTE prophylaxis between hospital admission and the day before the VTE diagnostic testing order date.', 2, -1, true),
('Oncology Colon Cancer: Chemotherapy for Stage III Colon Cancer Patients', '0385', 'Percentage of patients aged 18 years and older with Stage IIIA through IIIC colon cancer who are referred for adjuvant chemotherapy, prescribed adjuvant chemotherapy, or have previously received adjuvant chemotherapy within the 12 month reporting period.', 1, -1, true),
('Oncology Breast Cancer: Hormonal Therapy for Stage IC- IIIC Estrogen Receptor/Progesterone Receptor (ER/PR) Positive Breast Cancer', '0387', 'Percentage of female patients aged 18 years and older with Stage IC through IIIC, ER or PR positive breast cancer who were prescribed tamoxifen or aromatase inhibitor (AI) during the 12-month reporting period.', 1, -1, true),
('Prostate Cancer: Avoidance of Overuse of Bone Scan for Staging Low Risk Prostate Cancer Patients', '0389', 'Percentage of patients, regardless of age, with a diagnosis of prostate cancer at low risk of recurrence receiving interstitial prostate brachytherapy, OR external beam radiotherapy to the prostate, OR radical prostatectomy, OR cryotherapy who did not have a bone scan performed at any time since diagnosis of prostate cancer.', 1, -1, true),
('Adult Weight Screening and Follow-up', '0421', 'Percentage of patients aged 18 years and older with a calculated BMI in the past six months or during the current visit documented in the medical record AND if the most recent BMI is outside parameters, a follow-up plan is documented.', 1, -1, true),
